import React, { useState } from 'react';
import { IoHammerSharp } from 'react-icons/io5'
let ApartmentItem = (props) => {

    const {id,title,beds,days,price,deleteApartment, updateApartment} = props

    // Стан для обробки редагованих полів
    const [editMode, setEditMode] = useState(false);
    const [updatedTitle, setUpdatedTitle] = useState(title);
    const [updatedBeds, setUpdatedBeds] = useState(beds);
    const [updatedDays, setUpdatedDays] = useState(days);
    const [updatedPrice, setUpdatedPrice] = useState(price);

    const handleUpdate = () => {
        // Викликаємо функцію updateApartment, передану через пропси
        updateApartment(id, {
            title: updatedTitle,
            beds: updatedBeds,
            days: updatedDays,
            price: updatedPrice
        });
        setEditMode(false);
    };

    const dollarPrice = '$'+price
    return (
        <div className='apartmentItem'>
            <div className='innerContainer'>
                {editMode ? (
                    <>
                        <input
                            type='text'
                            value={updatedTitle}
                            onChange={(e) => setUpdatedTitle(e.target.value)}
                        />
                        <input
                            type='number'
                            value={updatedBeds}
                            onChange={(e) => setUpdatedBeds(e.target.value)}
                        />
                        <input
                            type='number'
                            value={updatedDays}
                            onChange={(e) => setUpdatedDays(e.target.value)}
                        />
                        <input
                            type='number'
                            value={updatedPrice}
                            onChange={(e) => setUpdatedPrice(e.target.value)}
                        />
                        <button onClick={handleUpdate} className='saveButton'>Зберегти</button>
                    </>
                ) : (
                    <>
                        <p className='text'>{title}</p>
                        <span>{beds}</span>
                        <span>{days}</span>
                        <span>{dollarPrice}</span>
                            <IoHammerSharp onClick={() => setEditMode(true)} className='update-icon' />
                    </>
                )}
                <button onClick={() => deleteApartment(id)} className='closeButton'>&times;</button>
                <button className='rentButton'>Rent</button>
            </div>
        </div>
    );
}

export default ApartmentItem;
